#!/bin/bash\n# Script para converter imagens PNG para WebP com qualidade otimizada\n# Uso: bash optimize-images.sh\n\necho \"üñºÔ∏è  Otimizando imagens para web...\"\necho \"\"\n\n# Verificar se ImageMagick est√° instalado\nif ! command -v magick &> /dev/null; then\n    echo \"‚ùå ImageMagick n√£o encontrado. Instale com: choco install graphicsmagick\"\n    exit 1\nfi\n\n# Vari√°veis\nQUALITY=80\nSOURCE_DIR=\".\"\nFILES_CONVERTED=0\n\n# Encontrar e converter PNGs\necho \"üîç Procurando arquivos PNG...\"\nfor file in $(find \"$SOURCE_DIR\" -type f -name \"*.png\" -not -path \"*/node_modules/*\"); do\n    webp_file=\"${file%.png}.webp\"\n    \n    if [ -f \"$webp_file\" ]; then\n        echo \"‚è≠Ô∏è  Pulando $file (WebP j√° existe)\"\n    else\n        echo \"üì¶ Convertendo: $file -> $webp_file\"\n        magick convert \"$file\" -quality $QUALITY \"$webp_file\"\n        \n        if [ -f \"$webp_file\" ]; then\n            # Calcular compress√£o\n            original_size=$(stat -f%z \"$file\" 2>/dev/null || stat -c%s \"$file\" 2>/dev/null)\n            compressed_size=$(stat -f%z \"$webp_file\" 2>/dev/null || stat -c%s \"$webp_file\" 2>/dev/null)\n            percent=$((100 - (compressed_size * 100 / original_size)))\n            \n            echo \"‚úÖ Convertido: $(basename \"$webp_file\") [compress√£o: ${percent}%]\"\n            FILES_CONVERTED=$((FILES_CONVERTED + 1))\n        else\n            echo \"‚ùå Erro ao converter: $file\"\n        fi\n    fi\ndone\n\necho \"\"\necho \"‚ú® Processo conclu√≠do!\"\necho \"üìä Arquivos criados: $FILES_CONVERTED\"\necho \"\"\necho \"üéØ Pr√≥ximos passos:\"\necho \"1. Revisar os arquivos .webp gerados\"\necho \"2. Usar ResponsiveImage.jsx para servir WebP com fallback PNG\"\necho \"3. Testar no https://pagespeed.web.dev/\"\n"